ruleSearch.bookList:
@js:JSON.parse(result).data.map(o=>{
    var detail = 'https://img.midukanshu.com/browser/pro/book/chapterlist/' + o.book_id + '.txt';
    var J = JSON.parse(java.ajax(detail));
    return {
      name:o.title,
      author:o.author,
      intro: o.description.trim(),
      latest: J.title,
      tags: o.category,
      cover: o.cover,
      catalogUrl: 'https://img.midukanshu.com/browser/pro/book/chapterlist/' + o.book_id + '.txt'
    }
});

