  };
});

ruleSearch.bookList:
.search-list-note + .list-book-col1 li
@js:
result.toArray().slice(0, 5).map(o =>{
    var oText = selector =>String(o.select(selector).text().trim()).replace(/.+：/, '');
    return {
        name: oText('.book-title'),
        author: o.select('.book-info-sub').eq(0).text(),
        tags: oText('.book-info-sub:gt(0)').replace(/\s+/g,',').replace(/已|中/, ''),
        latest: oText('.book-chapter').replace(/[\(（].*[求更谢乐发推].*[）\)]/,''),
        intro: oText('.book-summary'),
        cover: o.select('.book-cover img').attr('src') || 'http://suo.im/6mlA9F',
        catalogUrl: o.select('.book-title').attr('href')
    }
}).filter(v =>v.name);

@css:#novel-list li:not(:first-child)
<js>
result.toArray().slice(0,5).map(o=>{
    var detail = String(o.select('.col-xs-3 a').attr('href')).replace(/^\//, 'http://ewenxue.org/');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    return {
       name:  o.select('.col-xs-3').text(),
       author:o.select('.col-xs-2').first().text(),
       tags:  o.select('.col-xs-1').text(),
       latest: String(o.select('.col-xs-4').text()).replace(/(正文)?(\s+|_)/,'').replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'') +String(o.select('.time').text()).replace(/(\d+-\d+-\d+).*/,'（$1）'),
       intro: String(J.select('#shot').text()).trim(),
       cover: J.select('.col-xs-2 img').attr('src'),
       catalogUrl: J.select('.tac a').attr('href')
    }
});
