ruleToc.nextTocUrl:
@js:
list=[];
if(!(baseUrl.match(/series/)||baseUrl.match(/ebook/)||baseUrl.match(/comments/))){
if(baseUrl.match(/author/)){
if(result.match(/class="count">\(共(\d+)条\)/)){
count=result.match(/class="count">\(共(\d+)条\)/)[1];
page=parseInt(count/10);
for(i=0;i<page;i++){
list.push(baseUrl+'&start='+(i*10))
}}}else{
if(result.match(/data-total-page="([^"]+)">/)){
p=result.match(/data-total-page="([^"]+)">/)[1];
page=20;
if(p<page){
page=p
}
for(i=1;i<=page;i++){
list.push(baseUrl+'?start='+((i-1)*20))
}}}}
list

ruleSearch.bookList:
.list li
<js>
result
.toArray()
.slice(0,3)
.map(el =>{

    var url = String(el.select('a:eq(0)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);

    var J = org.jsoup.Jsoup.parse(java.ajax(url));

    var o = selector =>String(el.select(selector).text());

    var g = selector =>String(J.select(selector).text());

    var og = selector =>String(J.select('[property="og:' + selector + '"]').attr('content'));

    return {
      name: og('novel:book_name').replace(/(全文|免费阅读|最新章节).*|（.*）/,''),

      author: og('novel:author'),


      kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/,''),

      wordCount: g('.layui-bg-red').replace(/\s+万字/,'万'),

      latest: og('novel:latest_chapter_name').replace(/[\(（【].*[求更谢乐发推].*/,'') + ' • ' +  og('novel:update_time').replace(/(T|\s).*/,' ').replace(/\//g,'-'), 

      intro: og('description').replace(/.*(观看小说:|简介：)|各位书友.*/,'').replace(/\s+/g,''),

      cover: og('image').replace('s:',':'),

      url: url,
    };
  });
</js>

